    if(node1.children.size()!=node2.children.size()) return false;

    for(int i=0, j=node1.children.size()-1; i<=j; i++,j--){
        Node a=node1.children.get(i);
        Node b=node2.children.get(j);

        if(!isFoldable(a,b)) return false;
    }
    return true;
}

