        if(max == entry.getValue()) {
            retStr =  rand.nextBoolean() ? entry.getKey() : retStr;
        }
        if(max < entry.getValue()) {
            max  = entry.getValue();
            retStr = entry.getKey();
        }
    }
    return  retStr;

