@Override
public /* non-final */ <Z> List<P> fetchRange(Field<Z> field, Z lowerInclusive, Z upperInclusive) {
    return ctx()
            .selectFrom(table)
            .where(
                lowerInclusive == null
              ? upperInclusive == null
                ? noCondition()
                : field.le(upperInclusive)
              : upperInclusive == null
                ? field.ge(lowerInclusive)
                : field.between(lowerInclusive, upperInclusive))
            .fetch(mapper());
}

