public class UserDao {
    @Transactional
    public User create(User user) {
        EntityManager em = JPA.em();
        em.persist(user);
        return user;
    }

    @Transactional
    public User findById(int uid) {
        return JPA.em().find(User.class, uid);
    }

    @Transactional
    public User findBySub(String sub) {
        Query query  = JPA.em().createQuery("select u from User u where u.googleId = " + ":sub");
        query.setParameter("sub", sub);
        List<User> users = query.getResultList();
        if (users.size() == 0) return null;
        return users.get(0);
    }

    @Transactional
    public User findByEmail(String email) {
        Query query  = JPA.em().createQuery("select u from User u where u.email = " + ":email");
        query.setParameter("email", email);
        List<User> users = query.getResultList();
        if (users.size() == 0) return null;
        return users.get(0);

