            .queueUrl(queueUrl)
            .attributeNames(QueueAttributeName.APPROXIMATE_NUMBER_OF_MESSAGES_NOT_VISIBLE)
            .build());
    String v = attrs.attributes().get(QueueAttributeName.APPROXIMATE_NUMBER_OF_MESSAGES_NOT_VISIBLE);
    int parsed = 0;
    try { parsed = Integer.parseInt(v); } catch (Exception ignored) {}
    return parsed;
}

