        if(!countMap.containsKey(str)) {
            countMap.put(str, 0);
        }
        countMap.put(str, countMap.get(str) +1);
    }
    int max = -1;
    String retStr = null;
    for(Map.Entry<String, Integer> entry : countMap.entrySet()) {
        if(max == entry.getValue()) {
            retStr =  rand.nextBoolean() ? entry.getKey() : retStr;
        }
        if(max < entry.getValue()) {
            max  = entry.getValue();
            retStr = entry.getKey();
        }
    }
