  for (int i = 1; i < s.size(); i++)
  { Comparable next = v.get(i);
    if (largest.compareTo(next) < 0)
    { largest = next;
      res.clear();
      res.add(s.get(i));
    }
    else if (largest.compareTo(next) == 0)
    { res.add(s.get(i)); }
  }
  return res;
}

