public class InventoryManager {
    private DatabaseConnection dbConnection;

    public void updateStockLevel(String productCode, int quantity) {
        String sql = "UPDATE inventory SET quantity = ? WHERE product_code = ?";
        try {
            PreparedStatement stmt = database.prepareStatement(sql);
            stmt.setInt(1, quantity);
            stmt.setString(2, productCode);
            int rowsUpdated = stmt.executeUpdate();
            logger.info("Updated {} rows for product {}", rowsUpdated, productCode);
        } catch (SQLException e) {
            logger.error("Database update failed", e);
        }
    }
}

