import io.grpc.stub.StreamObserver;

public class MetricsCollector {
    private final MetricsServiceGrpc.MetricsServiceStub metricsClient;

    public void reportMetrics(List<DataPoint> metrics) {
        MetricsRequest request = MetricsRequest.newBuilder()
            .addAllDataPoints(metrics.stream()
                .map(this::convertToProtobuf)
                .collect(Collectors.toList()))
            .build();
        metricsClient.reportMetrics(request, new StreamObserver<MetricsResponse>() {
            @Override
            public void onNext(MetricsResponse response) {
                logger.debug("Metrics reported successfully");
            }
        });
    }
}

