public class BackupService {
    private String sftpHost = "backup.server.com";

    public void transferBackupFiles(List<File> files) {
        Session sftpSession = createSftpConnection();
        ChannelSftp sftpChannel = (ChannelSftp) sftpSession.openChannel("sftp");
        sftpChannel.connect();

        for (File file : files) {
            sftpChannel.put(file.getPath(), "/backups/" + file.getName());
        }
    }
}

public class FileTransferManager {
    public boolean sendToRemoteServer(byte[] fileData, String remotePath) {
        try {
            FTPClient client = new FTPClient();
            client.connect(remoteHost, 21);
            client.enterLocalPassiveMode();
            client.storeFile(remotePath, new ByteArrayInputStream(fileData));
            return client.completePendingCommand();
        } catch (IOException e) {
            logger.error("FTP upload failed", e);
            return false;
