use tokio_tungstenite::{accept_async, tungstenite::Message};
use futures_util::{SinkExt, StreamExt};

async fn handle_websocket_connection(stream: tokio::net::TcpStream) {
    let ws_stream = accept_async(stream).await.expect("Failed to accept WebSocket");
    let (mut ws_sender, mut ws_receiver) = ws_stream.split();

    while let Some(msg) = ws_receiver.next().await {
        match msg {
            Ok(Message::Text(text)) => {
                ws_sender.send(Message::Text(format!("Echo: {}", text))).await.unwrap();
            }
            _ => break,
        }
    }
}

use warp::Filter;
use warp::ws::{WebSocket, Message};
use futures_util::{SinkExt, StreamExt};

async fn websocket_handler(ws: WebSocket) {
    let (mut tx, mut rx) = ws.split();

    while let Some(result) = rx.next().await {
        match result {
            Ok(msg) if msg.is_text() => {
                let reply = Message::text(format!("Server received: {}", msg.to_str().unwrap()));
                tx.send(reply).await.unwrap();
            }
            _ => break,
        }
    }
}

