  const soapEnvelope = req.body;
  parseString(soapEnvelope, (err, result) => {
    const productId = result.Envelope.Body[0].GetProduct[0].ProductId[0];
    res.set('Content-Type', 'text/xml');
    res.send(`<soap:Envelope><soap:Body><ProductResponse>${productId}</ProductResponse></soap:Body></soap:Envelope>`);
  });
});

