function submitTaxCalculation(income, deductions) {
  const wsdlEndpoint = 'https://tax.gov.api/TaxCalculatorService?wsdl';
  const postData = `<soapenv:Envelope><soapenv:Body><CalculateTax><Income>${income}</Income><Deductions>${deductions}</Deductions></CalculateTax></soapenv:Body></soapenv:Envelope>`;

  const options = {
    hostname: 'tax.gov.api',
    path: '/TaxCalculatorService',
    method: 'POST',
    headers: { 'Content-Length': postData.length }
  };

  return new Promise((resolve, reject) => {
    const req = https.request(options, (res) => resolve(res));
    req.write(postData);
    req.end();
  });
}

