func (m *MetricsCollector) StartUDPListener(port int) error {
	addr, _ := net.ResolveUDPAddr("udp", fmt.Sprintf(":%d", port))
	m.udpSocket, err := net.ListenUDP("udp", addr)
	if err != nil {
		return err
	}

	go m.handleIncomingMetrics()
	return nil
}

