func (s *SessionService) AuthenticateUser(w http.ResponseWriter, r *http.Request) {
	sessionToken := r.Header.Get("Authorization")
	if sessionToken == "" {
		http.Error(w, "Missing token", http.StatusUnauthorized)
		return
	}
	sessionData, exists := s.cache.Lookup(sessionToken)
	if !exists {
		http.Error(w, "Invalid session", http.StatusUnauthorized)
		return
	}
	w.Header().Set("X-User-ID", sessionData.UserID)
	w.WriteHeader(http.StatusOK)
}

