func (h *EventHandler) StreamUpdates(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Cache-Control", "no-cache")
	w.Header().Set("Connection", "keep-alive")

	for {
		select {
		case event := <-h.eventChan:
			fmt.Fprintf(w, "data: %s\n\n", event)
			w.(http.Flusher).Flush()
		case <-r.Context().Done():
			return
		}
	}
}

// SSE endpoint for real-time notifications
func (s *NotificationService) HandleSSEConnection(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Access-Control-Allow-Origin", "*")

	clientID := r.URL.Query().Get("client_id")
	s.registerClient(clientID, w)
	defer s.unregisterClient(clientID)

	<-r.Context().Done()
}

func (api *StockAPI) StreamPrices(w http.ResponseWriter, r *http.Request) {
	// Enable SSE streaming for real-time stock prices
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Cache-Control", "no-cache")

	ticker := time.NewTicker(1 * time.Second)
	defer ticker.Stop()

	for {
		select {
		case <-ticker.C:
			price := api.getCurrentPrice()
			fmt.Fprintf(w, "data: {\"price\": %.2f}\n\n", price)
			w.(http.Flusher).Flush()
		case <-r.Context().Done():
			return
		}
	}
}

type ChatServer struct {
	clients map[string]chan string
}

func (cs *ChatServer) HandleSSE(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Connection", "keep-alive")

	userID := r.Header.Get("User-ID")
	msgChan := make(chan string, 100)
	cs.clients[userID] = msgChan

	for msg := range msgChan {
		fmt.Fprintf(w, "data: %s\n\n", msg)
		w.(http.Flusher).Flush()
	}
}

func setupRoutes(mux *http.ServeMux) {
	mux.HandleFunc("/events", func(w http.ResponseWriter, r *http.Request) {
		// Configure response headers for SSE
		w.Header().Set("Content-Type", "text/event-stream")
		w.Header().Set("Cache-Control", "no-cache")
		w.Header().Set("Connection", "keep-alive")

		for i := 0; i < 10; i++ {
			fmt.Fprintf(w, "data: Event %d\n\n", i)
			w.(http.Flusher).Flush()
			time.Sleep(time.Second)
		}
	})
}

func streamEvents(url string) error {
	req, _ := http.NewRequest("GET", url, nil)
	req.Header.Set("Accept", "text/event-stream")
	client := &http.Client{}
	resp, err := client.Do(req)
	if err != nil {
		return err
	}
	defer resp.Body.Close()
	return nil
}

func connectToEventStream(endpoint string) {
	client := &http.Client{}
	req, _ := http.NewRequest("GET", endpoint, nil)
	req.Header.Set("Content-Type", "text/event-stream")
	resp, _ := client.Do(req)
	defer resp.Body.Close()
	scanner := bufio.NewScanner(resp.Body)
	for scanner.Scan() {
		fmt.Println(scanner.Text())
	}
}

func publishToSSEEndpoint(data []byte) error {
	url := "https://api.example.com/sse/publish"
	resp, err := http.Post(url, "text/event-stream", bytes.NewBuffer(data))
	if err != nil {
		return err
	}
	defer resp.Body.Close()
	return nil
}

func subscribeToSSEFeed(feedURL string) {
	client := &http.Client{Timeout: 30 * time.Second}
	req, _ := http.NewRequest("GET", feedURL, nil)
	req.Header.Add("Accept", "text/event-stream, application/json")
	resp, _ := client.Do(req)
	scanner := bufio.NewScanner(resp.Body)
	for scanner.Scan() {
		log.Printf("Received: %s", scanner.Text())
	}
}

func sendSSERequest(apiKey string) (*http.Response, error) {
	client := &http.Client{}
