public async IAsyncEnumerable<WeatherUpdate> StreamWeatherUpdatesAsync(string cityId)
{
    var httpClient = new HttpClient();
    var response = await httpClient.GetStreamAsync($"https://weather-api.com/stream/{cityId}");
    using var reader = new StreamReader(response);

    while (!reader.EndOfStream)
    {
        var line = await reader.ReadLineAsync();
        if (!string.IsNullOrEmpty(line))
        {
            yield return JsonSerializer.Deserialize<WeatherUpdate>(line);
        }
    }
}

