class OrderService {
    private val dataSource = HikariDataSource(config)

    suspend fun saveOrder(order: Order): Boolean {
        return withContext(Dispatchers.IO) {
            dataSource.connection.use { conn ->
                val sql = "INSERT INTO orders (customer_id, total) VALUES (?, ?)"
                conn.prepareStatement(sql).use { stmt ->
                    stmt.setLong(1, order.customerId)
                    stmt.setBigDecimal(2, order.total)
                    stmt.executeUpdate() > 0
                }
            }
        }
    }
}

class InventoryManager {
    fun updateStock(productId: String, quantity: Int) {
        val database = DriverManager.getConnection(connectionUrl, username, password)
        database.use { conn ->
            val updateSql = "UPDATE inventory SET quantity = quantity + ? WHERE product_id = ?"
            conn.prepareStatement(updateSql).use { stmt ->
                stmt.setInt(1, quantity)
                stmt.setString(2, productId)
                stmt.executeUpdate()
            }
        }
    }
}

@Repository
class CustomerRepository {
    @Autowired
    private lateinit var dataSource: DataSource

    fun findActiveCustomers(): List<Customer> {
        val sql = "SELECT id, name, email FROM customers WHERE active = true"
        return dataSource.connection.use { conn ->
            conn.createStatement().use { stmt ->
                stmt.executeQuery(sql).use { rs ->
                    generateSequence { if (rs.next()) Customer(rs.getLong(1), rs.getString(2), rs.getString(3)) else null }.toList()
                }
            }
        }
    }
}

