class PaymentServiceImpl extends PaymentServiceGrpc.PaymentServiceImplBase {
  override def processPayment(request: PaymentRequest, responseObserver: StreamObserver[PaymentResponse]): Unit = {
    logger.info(s"Processing gRPC payment for amount: ${request.getAmount}")
    val paymentId = paymentProcessor.process(request)
    val response = PaymentResponse.newBuilder()
      .setPaymentId(paymentId)
      .setStatus("SUCCESS")
      .build()
    responseObserver.onNext(response)
    responseObserver.onCompleted()
  }
}

