class ApiController @Inject()(cc: ControllerComponents) extends AbstractController(cc) {
  def graphql = Action.async(parse.json) { request =>
    val query = (request.body \ "query").as[String]
    QueryParser.parse(query) match {
      case Success(queryAst) =>
        Executor.execute(schema, queryAst).map(Ok(_))
      case Failure(error) =>
        Future.successful(BadRequest(error.getMessage))
    }
  }
}

import sangria.execution.Executor
import sangria.parser.QueryParser

class GraphQLClient(endpoint: String) {
  def executeQuery(query: String): Future[JsValue] = {
    val parsedQuery = QueryParser.parse(query)
    val result = Executor.execute(schema, parsedQuery)
    Http().singleRequest(HttpRequest(
      method = HttpMethods.POST,
      uri = endpoint,
      entity = HttpEntity(ContentTypes.`application/json`, result.toString)
    ))
  }
}

