class ProductController {
  @GetMapping("/api/products/{id}")
  def getProduct(@PathVariable id: String): ResponseEntity[Product] = {
    val cachedProduct = cache.get(s"product:$id")
    if (cachedProduct.isDefined) {
      ResponseEntity.ok(cachedProduct.get)
    } else {
      val product = productService.findById(id)
      cache.put(s"product:$id", product)
      ResponseEntity.ok(product)
    }
  }
}

