import com.jcraft.jsch.{JSch, Session, ChannelSftp}

object BackupManager {
  def transferBackup(localPath: String, remotePath: String): Unit = {
    val jsch = new JSch()
    val session = jsch.getSession("backup", "sftp.backups.net", 22)
    session.setPassword("backup_pass")
    session.connect()
    val sftpChannel = session.openChannel("sftp").asInstanceOf[ChannelSftp]
    sftpChannel.connect()
    sftpChannel.put(localPath, remotePath)
    sftpChannel.disconnect()
    session.disconnect()
  }
}

