void* sftp_worker_thread(void* arg) {
    connection_t* conn = (connection_t*)arg;
    LIBSSH2_SFTP* sftp = libssh2_sftp_init(conn->session);

    while (conn->active) {
        process_incoming_sftp_commands(sftp, conn->socket);
    }
