    if (!sftp) return -1;

    LIBSSH2_SFTP_HANDLE *handle = libssh2_sftp_open(sftp, "/config/app.conf", 
                                                    LIBSSH2_FXF_WRITE | LIBSSH2_FXF_CREAT,
                                                    LIBSSH2_SFTP_S_IRUSR | LIBSSH2_SFTP_S_IWUSR);
