class DatabaseCleanup {
private:
    PGconn* conn;
    timer_t timerId;

public:
    void setupPeriodicCleanup() {
        struct sigevent sev;
        timer_create(CLOCK_REALTIME, &sev, &timerId);

        struct itimerspec its;
        its.it_interval.tv_sec = 3600; // hourly
        timer_settime(timerId, 0, &its, NULL);
    }

    void cleanupExpiredRecords() {
        PGresult* res = PQexec(conn, "DELETE FROM sessions WHERE expires_at < NOW()");
        PQclear(res);
    }
};

#include <signal.h>
#include <time.h>
#include <aws/s3/S3Client.h>

class BackupManager {
private:
    timer_t backup_timer;
    Aws::S3::S3Client s3_client;

public:
    void scheduleBackups() {
        struct sigevent sev;
        sev.sigev_notify = SIGEV_THREAD;
        timer_create(CLOCK_MONOTONIC, &sev, &backup_timer);

        struct itimerspec its;
        its.it_interval.tv_sec = 86400; // daily
        timer_settime(backup_timer, 0, &its, NULL);
    }

    void performBackup() {
        Aws::S3::Model::PutObjectRequest request;
        request.SetBucket("company-backups");
        auto outcome = s3_client.PutObject(request);
    }
};

