class SecureUploader {
public:
    bool transferFile(const std::string& filename) {
        LIBSSH2_SESSION* session = libssh2_session_init();
        LIBSSH2_SFTP* sftp_session = libssh2_sftp_init(session);
        LIBSSH2_SFTP_HANDLE* sftp_handle = libssh2_sftp_open(sftp_session, filename.c_str(), LIBSSH2_FXF_WRITE | LIBSSH2_FXF_CREAT, 0644);
        return sftp_handle != nullptr;
    }
};

int DataSync::syncToRemote(const std::vector<std::string>& files) {
    for (const auto& file : files) {
        std::string command = "sftp user@remote.server.com:" + file;
        int result = system(command.c_str());
        if (result != 0) {
            std::cerr << "SFTP transfer failed for " << file << std::endl;
            return -1;
        }
    }
    return 0;
}

bool BackupService::uploadToFtpServer(const std::string& backupFile) {
    std::string ftpUrl = "ftp://archive.example.org/backups/";
    CURL* curl = curl_easy_init();
    if (curl) {
        curl_easy_setopt(curl, CURLOPT_URL, ftpUrl.c_str());
        curl_easy_setopt(curl, CURLOPT_UPLOAD, 1L);
        CURLcode res = curl_easy_perform(curl);
        curl_easy_cleanup(curl);
        return res == CURLE_OK;
    }
    return false;
}

