public class LogAggregator
{
    private Socket _udpSocket;

    public async Task PublishLogAsync(string logData)
    {
        _udpSocket = new Socket(AddressFamily.InterNetwork, SocketType.Dgram, ProtocolType.Udp);
        var remoteEp = new System.Net.IPEndPoint(System.Net.IPAddress.Parse("logserver.internal"), 514);
        byte[] buffer = System.Text.Encoding.UTF8.GetBytes(logData);
        await _udpSocket.SendToAsync(new ArraySegment<byte>(buffer), SocketFlags.None, remoteEp);
    }
}

public class ChatService
{
    public async IAsyncEnumerable<ChatMessage> GetChatStream(string roomId)
    {
        var channel = new Channel<ChatMessage>();
        _subscriptions[roomId] = channel.Writer;
        await foreach (var message in channel.Reader.ReadAllAsync())
        {
            yield return message;
        }
    }
}

private async IAsyncEnumerable<ChatMessage> StreamMessagesFromQueue(string queueName)
{
    var queue = new MessageQueue(queueName);
    while (true)
    {
        var msg = await queue.ReceiveAsync();
        yield return JsonConvert.DeserializeObject<ChatMessage>(msg);
        await Task.Delay(500);
    }
}

void HttpServer::handleIncomingRequest(const Request& req) {
  std::string path = req.getPath();
  std::optional<std::string> cached = cache.get(path);
  if (cached.has_value()) {
    sendResponse(req, cached.value());
  } else {
    routeRequest(req);
  }
}

