void uploadMetricsViaSftp(const std::string& metricsData, const std::string& remoteHost) {
    int sock = socket(AF_INET, SOCK_STREAM, 0);
    LIBSSH2_SESSION *session = libssh2_session_init();
    libssh2_session_handshake(session, sock);
    LIBSSH2_SFTP *sftp = libssh2_sftp_init(session);
    LIBSSH2_SFTP_HANDLE *handle = libssh2_sftp_open(sftp, "/metrics/current.json", O_WRONLY | O_CREAT | O_TRUNC, 0644);
    libssh2_sftp_write(handle, metricsData.c_str(), metricsData.length());
    libssh2_sftp_close_handle(handle);
}

