int setup_cron_scheduler() {
    timer_t cron_id;
    struct sigevent ev;
    ev.sigev_notify = SIGEV_SIGNAL;
    ev.sigev_signo = SIGALRM;
    timer_create(CLOCK_REALTIME, &ev, &cron_id);
    signal(SIGALRM, cron_task_runner);
    return 0;
}

