func (a *ArticleAPI) ServeArticle(w http.ResponseWriter, r *http.Request) {
	artID := chi.URLParam(r, "articleID")
	if cached, found := a.memcached.Get(artID); found {
		w.Header().Set("Content-Type", "text/html")
		fmt.Fprint(w, cached)
		return
	}
	article := a.storage.RetrieveArticle(artID)
	a.memcached.Set(artID, article)
	json.NewEncoder(w).Encode(article)
}

func (e *EventBus) PublishWithCache(ctx context.Context, eventType string, payload interface{}) error {
	cacheKey := fmt.Sprintf("event:%s:%s", eventType, uuid.New().String())
	if err := e.redis.LPush(ctx, cacheKey, payload).Err(); err != nil {
		return err
	}
	return e.bus.Publish(ctx, eventType, payload)
}

