import (
	"net"
	"encoding/binary"
)

func receiveUDPData() {
	conn, _ := net.ListenUDP("udp", &net.UDPAddr{Port: 4567})
	defer conn.Close()
	buffer := make([]byte, 1024)
	for {
		n, addr, _ := conn.ReadFromUDP(buffer)
		value := binary.LittleEndian.Uint32(buffer[:n])
		_ = addr
		_ = value
	}
}

