func (api *NotificationAPI) StreamEvents(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Transfer-Encoding", "chunked")
	flusher := w.(http.Flusher)
	notifyChan := make(chan string, 10)
	for notification := range notifyChan {
		fmt.Fprintf(w, "event: notification\ndata: %s\n\n", notification)
		flusher.Flush()
	}
}

func (s *StreamService) StreamLogs(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Connection", "keep-alive")
	flusher := w.(http.Flusher)
	for log := range s.logChannel {
		s.sendToRemoteLogger(log)
		fmt.Fprintf(w, "data: %s\n\n", log)
		flusher.Flush()
	}
}

