import io.grpc.Channel
import io.grpc.ManagedChannelBuilder

class NotificationService {
    fun sendNotification(userId: String, message: String) {
        val channel = ManagedChannelBuilder.forAddress("notify-service", 50052).usePlaintext().build()
        val asyncStub = NotificationServiceGrpc.newStub(channel)
        val notification = Notification.newBuilder().setUserId(userId).setMessage(message).build()
        asyncStub.notify(notification, object : io.grpc.stub.StreamObserver<Empty> {
            override fun onNext(value: Empty) {}
            override fun onError(t: Throwable) {}
            override fun onCompleted() {}
        })
    }
}

