class OrderStatusTracker {
    fun trackOrderUpdates(orderId: Long): SseEmitter {
        val sseEmitter = SseEmitter()
        Thread {
            val status = orderServiceClient.getStatus(orderId)
            val shipmentInfo = externalShippingAPI.track(orderId)
            sseEmitter.send(SseEmitter.event().data(status).name("order_update").build())
            sseEmitter.send(SseEmitter.event().data(shipmentInfo).name("shipment").build())
        }.start()
        return sseEmitter
    }
}

