import { RabbitMQ } from 'amqplib';
import NodeCache from 'node-cache';

const cache = new NodeCache({ stdTTL: 1800 });
const mqConnection = new RabbitMQ();

mqConnection.consume('task-queue', async (msg) => {
  const taskId = msg.content.toString();
  const cachedResult = cache.get(`task:${taskId}`);
  if (cachedResult) {
    console.log('Using cached task result:', cachedResult);
    msg.ack();
  } else {
    const result = await processTask(taskId);
    cache.set(`task:${taskId}`, result);
    msg.ack();
  }
});

import NodeCache from 'node-cache';

const cache = new NodeCache({ stdTTL: 600 });

function writeToCache(key: string, value: string): boolean {
