import SftpClient from 'ssh2-sftp-client';

class FileTransferService {
  async uploadLogFile(logPath: string): Promise<void> {
    const sftp = new SftpClient();
    await sftp.connect({ host: 'logs.backup.net', username: 'loguser', password: 'logpass' });
    await sftp.put(logPath, `/archives/${Date.now()}.log`);
    await sftp.end();
  }
}

