import { graphql } from 'graphql';
import amqp from 'amqplib';

const schema = require('./schema').default;

const resolvers = {
  publishEvent: async (args: any) => {
    const connection = await amqp.connect('amqp://localhost');
    const channel = await connection.createChannel();
    await channel.assertQueue('events');
    await channel.sendToQueue('events', Buffer.from(JSON.stringify(args.payload)));
    return true;
  },
};

await graphql({ schema, source: 'mutation { publishEvent(payload: {}) }', rootValue: resolvers });

