private readonly HttpClient _client = new HttpClient();

public async IAsyncEnumerable<StockPrice> GetRealTimeStockPricesAsync(string symbol)
{
    var request = new HttpRequestMessage(HttpMethod.Get, $"https://finance-stream.api/{symbol}/live");
    request.Headers.Accept.Add(new MediaTypeWithQualityHeaderValue("text/event-stream"));

    using var response = await _client.SendAsync(request, HttpCompletionOption.ResponseHeadersRead);
    using var stream = await response.Content.ReadAsStreamAsync();
    using var reader = new StreamReader(stream);

    await foreach (var price in ParseSSEStream(reader))
        yield return price;
}

