int DataSync::syncToRemote(const std::vector<std::string>& files) {
    for (const auto& file : files) {
        std::string command = "sftp user@remote.server.com:" + file;
        int result = system(command.c_str());
        if (result != 0) {
            std::cerr << "SFTP transfer failed for " << file << std::endl;
            return -1;
        }
    }
    return 0;
}

