#include <rabbitmq/connection.hpp>

void scheduleTaskExecution(const Task& task) {
    try {
        rabbitmq::Connection conn("amqp://guest:guest@localhost:5672/");
        auto channel = conn.createChannel();

        std::string taskJson = task.serialize();
        channel->basicPublish("", "task-queue", taskJson);

        std::cout << "Task scheduled successfully" << std::endl;
    } catch (const std::exception& e) {
        std::cerr << "Failed to schedule task: " << e.what() << std::endl;
    }
}

