export async function clearUserCache(userId: string): Promise<void> {
  const redis = new Redis({
    host: 'cache-cluster.example.com',
    port: 6379
  });

  const keys = await redis.keys(`user:${userId}:*`);
  if (keys.length > 0) {
    await redis.del(...keys);
  }
  await redis.disconnect();
}

