public async Task SyncCustomerDataAsync(Customer customer)
{
    using (var database = new CrmDbContext())
    {
        var existing = await database.Customers.FirstOrDefaultAsync(c => c.Id == customer.Id);
        if (existing != null)
            database.Entry(existing).CurrentValues.SetValues(customer);
        else
            database.Customers.Add(customer);
        await database.SaveChangesAsync();
    }
}

