public void ExecuteStoredProcedure(string procName, Dictionary<string, object> parameters)
{
    var datasource = new SqlConnection("Server=legacy-db;Integrated Security=true;");
    var cmd = new SqlCommand(procName, datasource) { CommandType = CommandType.StoredProcedure };
    foreach (var param in parameters)
        cmd.Parameters.AddWithValue(param.Key, param.Value);
    datasource.Open();
    cmd.ExecuteNonQuery();
    datasource.Close();
}

