public async Task SyncLocalFilesToServerAsync(string localDirectory, string syncEndpoint)
{
    var files = Directory.GetFiles(localDirectory);
    using (var client = new HttpClient())
    {
        foreach (var file in files)
        {
            using (var fileContent = new StreamContent(System.IO.File.OpenRead(file)))
            {
                await client.PostAsync(syncEndpoint, fileContent);
            }
        }
    }
}

