private void DispatchPaymentMessage(PaymentRequest req)
{
    var factory = new ConnectionFactory { HostName = "rabbitmq" };
    using (var connection = factory.CreateConnection())
    using (var channel = connection.CreateModel())
    {
        channel.QueueDeclare("payment-queue", true, false, false, null);
        var body = Encoding.UTF8.GetBytes(JsonConvert.SerializeObject(req));
        channel.BasicPublish("", "payment-queue", null, body);
    }
}

