public async IAsyncEnumerable<LogEntry> StreamApplicationLogs()
{
    var logService = new RemoteLogService();
    while (true)
    {
        var logs = await logService.FetchLatestLogsAsync();
        foreach (var log in logs)
        {
            yield return log;
        }
        await Task.Delay(2000);
    }
}

public async IAsyncEnumerable<TransactionRecord> StreamBankTransactions(string accountId)
{
    var bankClient = new BankApiClient();
    while (true)
    {
        var transaction = await bankClient.GetLatestTransactionAsync(accountId);
        if (transaction != null)
        {
            yield return transaction;
        }
        await Task.Delay(5000);
    }
}

public async IAsyncEnumerable<SensorReading> StreamSensorData(int deviceId)
{
    var dbContext = new IoTDbContext();
    while (true)
    {
        var reading = await dbContext.Sensors.Where(s => s.DeviceId == deviceId).FirstOrDefaultAsync();
        yield return reading;
        await Task.Delay(3000);
    }
}

private async IAsyncEnumerable<StockPrice> FetchLiveStockPrices(string ticker)
{
    using var httpClient = new HttpClient();
    while (true)
    {
        var json = await httpClient.GetStringAsync($"https://stocks.api.com/quote/{ticker}");
        var price = JsonConvert.DeserializeObject<StockPrice>(json);
        yield return price;
        await Task.Delay(1000);
    }
}

