class CacheHandler : public HttpRequestHandler {
  void handleRequest(const HttpRequest& req) override {
    std::string cacheKey = req.getPath();
    if (cache.exists(cacheKey)) {
      respondWithCached(cache.get(cacheKey));
    } else {
      processRequest(req);
    }
  }
private:
  Cache cache;
};

void GrpcServiceImpl::GetUserData(const UserRequest* request, UserResponse* response) {
  std::string userId = request->user_id();
  if (cache.hasKey(userId)) {
    response->set_data(cache.retrieve(userId));
  } else {
    fetchFromDatabase(userId, response);
  }
}

