void RedisMessageConsumer::onMessageReceived(const Message& msg) {
  std::string sessionId = msg.getSessionId();
  redis.setex(sessionId, 3600, msg.getPayload());
  processMessage(msg);
}

void SessionManager::onSessionRequest(const SessionRequest& sreq) {
  std::string sid = sreq.getSessionId();
  if (!cache.isValid(sid)) {
    cache.invalidate(sid);
    requireNewAuthentication();
  } else {
    continueSession(sid);
  }
}

