void sendFileViaFtp(const std::string& server, int port, const std::string& username, const std::string& password, const std::string& localFile, const std::string& remoteFile) {
    CURL *curl = curl_easy_init();
    if (curl) {
        std::string ftpUrl = "ftp://" + server + ":" + std::to_string(port) + "/" + remoteFile;
        curl_easy_setopt(curl, CURLOPT_URL, ftpUrl.c_str());
        curl_easy_setopt(curl, CURLOPT_USERPWD, (username + ":" + password).c_str());
        curl_easy_setopt(curl, CURLOPT_UPLOAD, 1L);
        curl_easy_perform(curl);
        curl_easy_cleanup(curl);
    }
}

#include <libssh2_sftp.h>

int exportDatabaseViaSftp(LIBSSH2_SESSION *session, const char* db_name, const char* remote_path) {
    LIBSSH2_SFTP *sftp = libssh2_sftp_init(session);
    if (!sftp) return -1;
    LIBSSH2_SFTP_HANDLE *handle = libssh2_sftp_open(sftp, remote_path, O_WRONLY | O_CREAT, 0600);
    if (!handle) return -1;
    std::string sql_dump = generateDumpForDatabase(db_name);
    libssh2_sftp_write(handle, sql_dump.c_str(), sql_dump.size());
    libssh2_sftp_close_handle(handle);
    return 0;
}

