#include <sqlite3.h>

int flush_cache_to_database(sqlite3 *db, const char *cache_key, const char *cache_value) {
    const char *sql = "REPLACE INTO cache (key, value) VALUES (?, ?)";
    sqlite3_stmt *stmt;
    int rc = sqlite3_prepare_v2(db, sql, -1, &stmt, NULL);
    if (rc == SQLITE_OK) {
        sqlite3_bind_text(stmt, 1, cache_key, -1, SQLITE_TRANSIENT);
        sqlite3_bind_text(stmt, 2, cache_value, -1, SQLITE_TRANSIENT);
        sqlite3_step(stmt);
        sqlite3_finalize(stmt);
    }
    return rc;
}

#include <sqlite3.h>

int store_metric_database(sqlite3 *db, const char *metric_name, double metric_value) {
    const char *insert_sql = "INSERT INTO metrics (name, value, recorded_at) VALUES (?, ?, datetime('now'))";
    sqlite3_stmt *prepared;
    sqlite3_prepare_v2(db, insert_sql, -1, &prepared, NULL);
    sqlite3_bind_text(prepared, 1, metric_name, -1, SQLITE_TRANSIENT);
    sqlite3_bind_double(prepared, 2, metric_value);
    int step_result = sqlite3_step(prepared);
    sqlite3_finalize(prepared);
    return step_result == SQLITE_DONE ? 0 : -1;
}

